import React, { useState } from 'react';
import axios from 'axios';

function CreateProject() {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [deadline, setDeadline] = useState('');
    const [tasks, setTasks] = useState([{ description: '', completed: false }]);

    const handleTaskChange = (index, key, value) => {
        const newTasks = [...tasks];
        newTasks[index][key] = value;
        setTasks(newTasks);
    };

    const handleSubmit = async () => {
        await axios.post('http://localhost:5000/api/projects', { title, description, deadline, tasks });
        alert('Project created successfully!');
    };

    return (
        <div>
            <h1>Create a New Project</h1>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Project Title" />
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Project Description" />
            <input type="date" value={deadline} onChange={(e) => setDeadline(e.target.value)} placeholder="Deadline" />
            {tasks.map((task, index) => (
                <div key={index}>
                    <input
                        type="text"
                        value={task.description}
                        onChange={(e) => handleTaskChange(index, 'description', e.target.value)}
                        placeholder="Task Description"
                    />
                </div>
            ))}
            <button onClick={() => setTasks([...tasks, { description: '', completed: false }])}>Add Task</button>
            <button onClick={handleSubmit}>Create Project</button>
        </div>
    );
}

export default CreateProject;
